import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import axios from "axios";

const BlogDetail = () => {

  const [blog, setblog] = useState();

  const id = useParams().id;
  console.log(id);

  const [inputs, setInputs] = useState({
    title: "",
    description: "",
    image: "",
  });

  const handleChange = (e) => {
    setInputs((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  };

  const fetchDetails = async () => {
    const res = await axios.get(`http://localhost:5000/api/blog/${id}`).catch(err => console.log(err));
    const data = await res.data;
    return data;
  }

  useEffect(()=>{
    fetchDetails().then(data=>setblog(data.blog));
  },[id]);
  console.log(blog);

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <Box
          border={"none"}
          borderRadius={5}
          boxShadow="10px 10px 20px #ccc"
          bgcolor="#f2ead3"
          padding={3}
          margin={"auto"}
          marginTop={3}
          display="flex"
          flexDirection={"column"}
          width={"80%"}
        >
          <Typography
            //className={classes.font}
            fontWeight={"bold"}
            padding={3}
            color="#391809"
            variant="h4"
            textAlign={"center"}
          >
            Post Your Blog
          </Typography>
          <InputLabel //className={classes.font} 
          sx={labelStyles}>
            Title
          </InputLabel>
          <TextField
            //className={classes.font}
            name="title"
            onChange={handleChange}
            value={inputs.title}
            margin="normal"
            variant="outlined"
          />
          <InputLabel //className={classes.font} 
          sx={labelStyles}>
            Description
          </InputLabel>
          <TextField
            //className={classes.font}
            name="description"
            onChange={handleChange}
            value={inputs.description}
            margin="normal"
            variant="outlined"
          />
          <InputLabel //className={classes.font} 
          sx={labelStyles}>
            ImageURL
          </InputLabel>
          <TextField
            //className={classes.font}
            name="image"
            onChange={handleChange}
            value={inputs.image}
            margin="normal"
            variant="outlined"
          />
          <Button
            sx={{ mt: 2, borderRadius: 2, bgcolor:"#a77c64", color:"white", marginLeft:"20%", marginRight:"20%" }}
            variant="text"
            color="inherit"
            type="submit"
          >
            Submit
          </Button>
        </Box>
      </form>
    </div>
  )
}

export default BlogDetail
