import React, { useState } from 'react'
import { AppBar, Typography, Toolbar, Box, Button, Tabs, Tab } from '@mui/material'
import { Link } from 'react-router-dom'
import {useSelector} from 'react-redux'

const Header = () => {
    const isLoggedIn = useSelector(state => state.isLoggedIn);
    const [value, setvalue] = useState();
    return (
        <AppBar position='sticky' sx={{ background: "linear-gradient(90deg, rgba(57,24,9,1) 0%, rgba(167,124,100,1) 85%)" }}>
            <Toolbar>
                <Typography variant='h4'>Blogg</Typography>
                {isLoggedIn && <Box display="flex" marginLeft="auto" marginRight="auto">
                    <Tabs textColor='inherit' value={value} onChange={(e, val) => setvalue(val)}>
                        <Tab LinkComponent={Link} to="/blogs" label="All Blogs" />
                        <Tab LinkComponent={Link} to="/myBlogs" label="My Blogs" />
                    </Tabs>
                </Box>}
                <Box display="flex" marginLeft="auto">
                    {!isLoggedIn && <Button LinkComponent={Link} to="/auth" variant='text' sx={{ margin: 1, borderRadius: 2 }} style={{ color: 'white' }}>Login</Button>}
                    {!isLoggedIn && <Button LinkComponent={Link} to="/auth" variant='text' sx={{ margin: 1, borderRadius: 2 }} style={{ color: 'white' }}>Signup</Button>}
                    {isLoggedIn && <Button LinkComponent={Link} to="/auth" variant='text' sx={{ margin: 1, borderRadius: 2 }} style={{ color: 'white' }}>Logout</Button>}
                </Box>
            </Toolbar>
        </AppBar>
    )
}

export default Header
